let replaceTimeout;

function replaceVideos() {
    clearTimeout(replaceTimeout);
    replaceTimeout = setTimeout(() => {
        document.querySelectorAll('video').forEach(video => {
            try {

                // èŽ·å–è§†é¢‘æº
                const videoSrc = video.currentSrc || video.getAttribute('src');
                if (!videoSrc) return;
                        const posterSrc = video.poster || video.getAttribute('poster') || '';

                // åˆ›å»ºå®¹å™¨ï¼ˆç»§æ‰¿æ ·å¼ï¼‰
                var container = document.createElement('div');
                container.id = generateUniqueId();
                container.style.width = '100%';
                //container.className = video.className;
                container.style.aspectRatio = '16/9';

                // æ›¿æ¢å…ƒç´ 
                video.parentNode.replaceWith(container);

                // åˆå§‹åŒ–æ’­æ”¾å™¨ï¼ˆç»§æ‰¿é…ç½®ï¼‰
                var art = new Artplayer({
                    container: '#' + container.id,
                    url: videoSrc,
                    poster:posterSrc,
                    theme: '#ffad00',
                    autoplay: false,
                    muted: false,
                    fullscreen: true,
                    autoSize: true,
                    autoMini: false,
                    playbackRate: true,
                    setting: true,
                    lock: true,
                    gesture: true,
                    fastForward: true,
                    autoPlayback: true,
                    autoOrientation: true,
                });

                art.on('dblclick', (event) => {
                    event.stopImmediatePropagation();
                    event.stopPropagation();
                    event.preventDefault();
                    const tmz = +"20";
                    const clix = event.clientX;
                    const rwid = art.rect.width;
                    if (clix < rwid / 3) art.backward = tmz;
                    else if (clix > rwid * 2 / 3) art.forward = tmz;
                    else art.toggle();
                });

            } catch (e) {
                console.log('ðŸŒˆðŸŒˆðŸŒˆArtPlayer init error:' + e);
            }
        });
    }, 100);
}

// åˆå§‹æ‰§è¡Œ
replaceVideos();

// é¡µé¢å¸è½½æ—¶æ¸…ç†
window.addEventListener('beforeunload', () => {
    clearTimeout(replaceTimeout);
});

