http@js:`data:text/html;charset=utf-8,<html>

<head>
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>导入</title>
    <style>
        body {
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            height: 90vh;
        }

        textarea {
            font-size: 12px;
            width: 96%;
            margin: 10px 2%;
            padding: 6px;
        }

        button {
            background-color: rgb(76, 175, 80);
            color: white;
            border: none;
            border-radius: 4px;
            height: 60px;
            width: 120px;
            overflow: hidden;
            transition: font-size 0.3s;
            font-size: 16px;
        }

        .an-s {
            font-size: 12px;
        }
    </style>
</head>

<body>
    <textarea id="copyt" rows="6" placeholder="在这里填写链接\n支持书源链接、订阅源链接、净化规则链接、字典规则链接、TXT目录规则链接导入\n支持书籍网址导入(需含有网址对应的书源)"></textarea>
    <br>
    <button id="btn1" onclick="copy()">导入</button>
    <script>
        function copy() {
            const btn = document.getElementById('btn1');
            btn.classList.add('an-s');
            const cpt = document.getElementById("copyt");
            let url = cpt.value;
            url = url.replace(/\\n|\\r|^\\s|\\s$/g, '');
            if (url == '') alert('请输入内容');
            else if (/^https?:/.test(url)) {
                if (/json$/.test(url)) {
                    window.location.href = 'legado://import/importonline?src=' + url;
                }
                else {
                    window.location.href = 'legado://import/addToBookshelf?src=' + url;
                }
            }
            else alert('链接格式错误');
            setTimeout(() => {
                btn.classList.remove('an-s');

            }, 450);
        }
    </script>
</body>

</html>`
