	java.longToast('\n出错了，请再次尝试刷新分类');
	}
let bylj = [];
let byljt = '';
let bb = source.get('bblyc');
if (bb==1) byljt = '<th style="width: 15%;">备用导入</th>';
for (char of ur) {
if (bb==1) {
	bylj.push(`<td><a href="legado://import/Source?src=${_备用链接.match(/https:\/\/github.com\/[^\/]+\/[^\/]+/)[0] + String(char).replace(/^\/[^\/]+\/[^\/]+/,'')}"><button><small>导入</small></button></a></td>`);
	} else {
		bylj.push('');
		}

}

let re = '';
for (let i = 0; i < nm.length; i++) {
	let xx= `<tr>
<td>${nm[i]}</td>
<td><i class="time">${String(tm[i]).replace(/T([^+]+)\+08.+/,' $1')}</i></td>
<td><a href="legado://import/Source?src=https://gitee.com${ur[i]}"><button><small>导入</small></button></a></td>
${bylj[i]}
<td><a href="https://gitee.com${ur[i].replace('/blob/','/commits/')}"  style="color:#0000c0;">日志</a></td>
</tr>`.replaceAll('/blob/','/raw/');
	re += xx;
	}

let bt = `<tr>
<th>文件名</th>
<th style="width: 38%;">更新时间</th>
