@js:
let nm = java.getStringList('$..blobs[*].name');
let ur = java.getStringList('$..blobs[*].url');
let tm = java.getStringList('$..blobs[*]*.time');
if (nm=='[]') {
	let kk = source.get('kklyc');
	kk == 1 ? source.put('kklyc',0) : source.put('kklyc',1);
	java.longToast('\n出错了，请尝试重新刷新分类');
	}
let re = '';
//ttt=JSON.stringify(this,null,4)
//java.log(ttt)
for (let i = 0; i < nm.length; i++) {
	let xx= `<tr align="center" style="color:#000090">
<td>${nm[i]}</td>
<td><i style="font-size:8pt">${String(tm[i]).replace(/T([^+]+)\+08.+/,' $1')}</i></td>
<td><a href="legado://import/Source?src=https://gitee.com${ur[i].replace('/blob/','/raw/')}" role="button"><button><small>一键导入</small></button></a></td>
<td><a href="https://gitee.com${ur[i].replace('/blob/','/commits/')}"  style="color:#0000c0">日志</a></td>
</tr>`;
	re += xx;
	}

let bt = `<tr style="color:#000080">
<th>文件名</th>
<th style="width: 33%;">更新时间</th>
<th style="width: 22%;">导入</th>
<th>更新日志</th>
</tr>`;

